<?phpclass Zend_View_Helper_Grouphelper{		protected $request;	public function Grouphelper($groupid)	{		$group_obj = new Application_Model_Groups();		$GroupRow=$group_obj->grouprow($groupid);				if($GroupRow['GroupPrivacy']=='1') $bgimage='icon_groupopen.png'; elseif($GroupRow['GroupPrivacy']=='3') $bgimage='icon_groupreq.png'; else $bgimage='icon_grouppriv.png';		// GROUP TYPE NAME		if($GroupRow['GroupType']!=0 && $GroupRow['GroupType']!='-1') {						$GroupTypeRow=$group_obj->getgrouptype($GroupRow['GroupType']);			$GroupTypeName=', '.$GroupTypeRow['TypeName'];		} elseif($GroupRow['GroupType']=='-1')		$GroupTypeName=', '.$GroupRow['GroupTypeOther'];		// GROUP TYPE NAME		return $GroupText='<div class="db-group-label"><div style="float:left"> <a href="/group/groupdetails/group/'.$groupid.'">group - '.$GroupRow['GroupName'].'</a>'.$GroupTypeName.'</div><div style="float:left; margin-left:5px; margin-top:-5px; width:77px; height:28px; background-image:url(images/'.$bgimage.'); background-repeat:no-repeat;"></div></div>';			}}?>